import { useEffect, useState } from "react";
import axios from 'axios';
import {Val} from './Home'

const Report = (links) => {
  const [data, setData] = useState('Loading....');
  const json = JSON.stringify(links);
  const [images, setImages] = useState([]);
  console.log(json);

  useEffect(() => {
    axios.post('http://127.0.0.1:5000/AI/answer',json, {
      params: {
        num: Val
      }
    })
  .then(response => {
    setData(response.data);
    console.log(response);
    return;
  })
  .catch(error => {
    console.error(error);
  });
  const intervals = [];

    links.forEach((link, index) => {
      const interval = setInterval(() => {
        const code = getAmazonCode(link);
        const imageUrl = `https://images-na.ssl-images-amazon.com/images/I/${code}.jpg`;

        setImages(prevImages => {
          const newImages = [...prevImages];
          newImages[index] = imageUrl;
          return newImages;
        });

      }, 10000);
      intervals.push(interval);
    });

    return () => {
      intervals.forEach(interval => clearInterval(interval));
    }

  }, [links]);

  // Function to convert new lines to HTML line breaks
  const formatDataWithNewlines = (text) => {
    return { __html: text.replace(/\n/g, '<br>') };
  };

  const getAmazonCode = (url) => {
    const matches = url.match(/dp\/([0-9]+)/);
    return matches ? matches[1] : null;
  }


  return (
    <div className="Report">
      <h2>Report</h2>
      <br />
      {data === 'Loading....' ? (
        <div>
          <p>{data}</p>
          <div className="loading-spinner">
            <div className="spinner"></div>
          </div>
          <div className="Pics">
            {images.map(image => (
              <img key={image} src={image} alt="Amazon product" />  
            ))}
          </div>
        </div>

      ) : (
        // Use dangerouslySetInnerHTML to render the data with new lines
        <p dangerouslySetInnerHTML={formatDataWithNewlines(data)}></p>
      )}
    </div>
  );  
  }

  export default Report;
