import { useEffect, useState } from "react";
import axios from 'axios';
import {Val} from './Home'

const Report = (links) => {
  const [data, setData] = useState('Loading....');
  const json = JSON.stringify(links);
  const [images, setImages] = useState([]);
  console.log(json);

  useEffect(() => {
    axios.post('http://127.0.0.1:5000/AI/answer',json, {
      params: {
        num: Val
      }
    })
  .then(response => {
    setData(response.data);
    console.log(response);
    return;
  })
  .catch(error => {
    console.error(error);
  });
  }, [links]);

  useEffect(() => {
    const fetchImages = async () => {
      const productImages = await Promise.all(products.map(async (product) => {
        const imageUrl = await fetchAmazonImage(product.link);
        return imageUrl;
      }));
      setImages(productImages);
    };

    fetchImages();
  }, [products]);

  const fetchAmazonImage = async (link) => {
    const amazonCode = getAmazonCode(link);
    if (amazonCode) {
      const imageUrl = `https://images-na.ssl-images-amazon.com/images/I/${amazonCode}.jpg`;
      return imageUrl;
    }
    return null;
  };

  const getAmazonCode = (url) => {
    const matches = url.match(/dp\/([0-9]+)/);
    return matches ? matches[1] : null;
  };

  // Function to convert new lines to HTML line breaks
  const formatDataWithNewlines = (text) => {
    return { __html: text.replace(/\n/g, '<br>') };
  };

  return (
    <div className="Report">
      <h2>Report</h2>
      <br />
      {data === 'Loading....' ? (
        <div>
          <p>{data}</p>
          <div className="loading-spinner">
            <div className="spinner"></div>
          </div>
          {/* <img src={imageUrl} alt="Amazon product" /> */}
        </div>

      ) : (
        // Use dangerouslySetInnerHTML to render the data with new lines
        <p dangerouslySetInnerHTML={formatDataWithNewlines(data)}></p>
      )}
    </div>
  );  
  }

  export default Report;
